// 경로 : src/App.js

import React from "react";
import CountButton from "./components/CountButton";
import Number from "./components/Number";
import styled from "styled-components";
import { connect } from "react-redux";
import { increase, decrease } from "./store/reducer";

const Wrapper = styled.div`
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100px;
  margin-top: 100px;
`;

const ButtonWrapper = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;
  margin-bottom: 50px;
`;

const App = ({ number, increase, decrease }) => {
  return (
    <Wrapper>
      <ButtonWrapper>
        <CountButton onClick={() => increase(number + 1)} text="+" />
        <CountButton onClick={() => decrease(number - 1)} text="-" />
      </ButtonWrapper>
      <Number number={number} />
    </Wrapper>
  );
};

const mapStateToProps = state => ({
  number: state.number
});

const mapDispatchToProps = dispatch => ({
  increase: number => dispatch(increase(number)),
  decrease: number => dispatch(decrease(number))
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
